step.script:
tmp['MSH']['MSH.4']['MSH.4.1'] = validate(msg['data']['sending_facility'], '', new Array());

tmp['MSH']['MSH.6']['MSH.6.1'] = validate(msg['data']['receiving_facility'], '', new Array());

tmp['PID']['PID.2']['PID.2.1'] = validate(msg['data']['patient']['national_patient_id'], '', new Array());

tmp['PID']['PID.5']['PID.5.2'] = validate(msg['data']['patient']['first_name'], '', new Array());

tmp['PID']['PID.5']['PID.5.1'] = validate(msg['data']['patient']['last_name'], '', new Array());

tmp['PID']['PID.5']['PID.5.3'] = validate(msg['data']['patient']['middle_name'], '', new Array());

tmp['OBR']['OBR.7']['OBR.7.1'] = validate(msg['data']['date_time'], '', new Array());

tmp['OBR']['OBR.16']['OBR.16.1'] = validate(msg['data']['who_order_test']['id_number'], '', new Array());

tmp['OBR']['OBR.16']['OBR.16.2'] = validate(msg['data']['who_order_test']['last_name'], '', new Array());

tmp['OBR']['OBR.16']['OBR.16.3'] = validate(msg['data']['who_order_test']['first_name'], '', new Array());

tmp['OBR']['OBR.4']['OBR.4.2'] = validate(msg['data']['test_type'], '', new Array());

tmp['SPM']['SPM.4']['SPM.4.2'] = validate(msg['data']['sample_type'], '', new Array());

// Loop Results

var keys = Object.keys(msg['data']['results']);

for(var i = 0; i < keys.length; i++) {

	tmp['OBX'][i]['OBX.16']['OBX.16.1'] = validate(msg['data']['results'][keys[i]]['entered_by']['id_number'], '', new Array());

	tmp['OBX'][i]['OBX.16']['OBX.16.2'] = validate(msg['data']['results'][keys[i]]['entered_by']['last_name'], '', new Array());

	tmp['OBX'][i]['OBX.16']['OBX.16.3'] = validate(msg['data']['results'][keys[i]]['entered_by']['first_name'], '', new Array());

	tmp['OBX'][i]['OBX.3']['OBX.3.2'] = validate(msg['data']['results'][keys[i]]['test_name'], '', new Array());

	tmp['OBX'][i]['OBX.5']['OBX.5.1'] = validate(msg['data']['results'][keys[i]]['result'], '', new Array());

	tmp['OBX'][i]['OBX.6']['OBX.6.1'] = validate(msg['data']['results'][keys[i]]['units'], '', new Array());

	tmp['OBX'][i]['OBX.7']['OBX.7.1'] = validate(msg['data']['results'][keys[i]]['reference_range'], '', new Array());

	tmp['OBX'][i]['OBX.16']['OBX.16.1'] = validate(msg['data']['results'][keys[i]]['entered_by']['id_number'], '', new Array());

	tmp['OBX'][i]['OBX.16']['OBX.16.2'] = validate(msg['data']['results'][keys[i]]['entered_by']['last_name'], '', new Array());

	tmp['OBX'][i]['OBX.16']['OBX.16.3'] = validate(msg['data']['results'][keys[i]]['entered_by']['first_name'], '', new Array());

	tmp['OBX'][i]['OBX.11']['OBX.11.1'] = validate(msg['data']['results'][keys[i]]['status'], '', new Array());

	tmp['OBX'][i]['OBX.10']['OBX.10.1'] = validate(msg['data']['results'][keys[i]]['rejection_reason'], '', new Array());

	tmp['NTE'][i]['NTE.3']['NTE.3.1'] = validate(msg['data']['results'][keys[i]]['remark'], '', new Array());

}

// logger.info(tmp['OBX'].length());

for(var i = parseInt(tmp['OBX'].length()) - 1; i >= keys.length; i--) {

	delete tmp['OBX'][i];

	delete tmp['NTE'][i];

}

